//SPDX-License-Identifier: MIT
pragma solidity >= 0.8.20;

import "@openzeppelin/contracts/utils/Base64.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

library URIEncoding {
    using Strings for uint256;

    function generateURI(
        string memory title,
        string memory content,
        address creator,
        address collection,
        uint256 tokenId
    ) public pure returns (string memory) {
        string memory svg = generateSVG(title);
        string memory dynamicUrl = string(
            abi.encodePacked(
                "www.sugar.xyz/",
                Strings.toHexString(uint160(collection), 20),
                "/",
                Strings.toString(tokenId)
            )
        );

        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(
                        bytes(
                            abi.encodePacked(
                                '{"name":"',
                                title,
                                '","description":"A fully onchain post you can read on ',
                                dynamicUrl,
                                '",',
                                '"author":"',
                                creator,
                                '",',
                                '"image":"',
                                svg,
                                '",',
                                '"content":"',
                                content,
                                '"}'
                            )
                        )
                    )
                )
            );
    }

    function generateSVG(
        string memory title
    ) internal pure returns (string memory) {
        // Define SVG parameters
        uint256 width = 400;
        uint256 height = 400;
        uint256 fontSize = 24;
        uint256 lineHeight = 30;
        uint256 padding = 40;
        uint256 footerFontSize = 12;
        uint256 maxWidth = width - (2 * padding);

        // Wrap text
        string[] memory lines = wrapText(title, maxWidth / (fontSize / 2)); // Approximate characters per line

        // Calculate text block height
        uint256 textHeight = lines.length * lineHeight;
        // Calculate footer position
        uint256 footerY = height - padding - (footerFontSize / 2);
        uint256 mainTextBottom = ((height - textHeight) / 2) +
            textHeight +
            lineHeight;
        if (footerY < mainTextBottom) {
            footerY = mainTextBottom + (footerFontSize / 2);
        }

        // Start SVG
        string memory svg = string(
            abi.encodePacked(
                '<svg xmlns="http://www.w3.org/2000/svg" width="',
                width.toString(),
                '" height="',
                height.toString(),
                '" viewBox="0 0 ',
                width.toString(),
                " ",
                height.toString(),
                '">',
                '<rect width="100%" height="100%" fill="#fafaf9"/>',
                "<style>.text { fill: #000; font-family: Courier New, monospace; font-size: ",
                fontSize.toString(),
                "px; }",
                ".footer { fill: #000; font-family: Courier New, monospace; font-size: ",
                footerFontSize.toString(),
                "px;}</style>"
            )
        );

        // Add text lines
        uint256 yPosition = (height - textHeight) / 2 + fontSize; // Center text block vertically
        for (uint i = 0; i < lines.length; i++) {
            svg = string(
                abi.encodePacked(
                    svg,
                    '<text x="50%" y="',
                    yPosition.toString(),
                    '" class="text" text-anchor="middle">',
                    escapeTitle(lines[i]),
                    "</text>"
                    '<text x="50%" y="',
                    footerY.toString(),
                    '" class="footer" text-anchor="middle">',
                    "Read it on sugar.xyz",
                    "</text>"
                )
            );
            yPosition += lineHeight;
        }

        // Close SVG
        svg = string(abi.encodePacked(svg, "</svg>"));

        string memory svgBase64 = Base64.encode(bytes(svg));
        return
            string(abi.encodePacked("data:image/svg+xml;base64,", svgBase64));
    }

    function wrapText(
        string memory text,
        uint256 maxLineLength
    ) internal pure returns (string[] memory) {
        bytes memory textBytes = bytes(text);
        uint256 numLines = (textBytes.length + maxLineLength - 1) /
            maxLineLength; // Round up
        string[] memory lines = new string[](numLines);

        uint256 lineStart = 0;
        for (uint256 i = 0; i < numLines; i++) {
            uint256 lineEnd = lineStart + maxLineLength;
            if (lineEnd > textBytes.length) {
                lineEnd = textBytes.length;
            } else {
                // Find the last space before maxLineLength
                while (lineEnd > lineStart && textBytes[lineEnd - 1] != 0x20) {
                    lineEnd--;
                }
                if (lineEnd == lineStart) {
                    lineEnd = lineStart + maxLineLength; // Force break if no space found
                }
            }

            bytes memory lineBytes = new bytes(lineEnd - lineStart);
            for (uint256 j = 0; j < lineBytes.length; j++) {
                lineBytes[j] = textBytes[lineStart + j];
            }
            lines[i] = string(lineBytes);

            lineStart = lineEnd;
            if (lineStart >= textBytes.length) break;
        }

        return lines;
    }

    function escapeTitle(
        string memory _title
    ) internal pure returns (string memory) {
        bytes memory title = bytes(_title);
        bytes memory result = new bytes(title.length * 6);
        uint256 resultIndex = 0;

        for (uint256 i = 0; i < title.length; i++) {
            bytes1 char = title[i];
            if (char == "&") {
                for (uint j = 0; j < 5; j++)
                    result[resultIndex++] = bytes("&amp;")[j];
            } else if (char == "<") {
                for (uint j = 0; j < 4; j++)
                    result[resultIndex++] = bytes("&lt;")[j];
            } else if (char == ">") {
                for (uint j = 0; j < 4; j++)
                    result[resultIndex++] = bytes("&gt;")[j];
            } else if (char == '"') {
                for (uint j = 0; j < 6; j++)
                    result[resultIndex++] = bytes("&quot;")[j];
            } else if (char == "'") {
                for (uint j = 0; j < 6; j++)
                    result[resultIndex++] = bytes("&#39;")[j];
            } else {
                result[resultIndex++] = char;
            }
        }

        bytes memory finalResult = new bytes(resultIndex);
        for (uint256 i = 0; i < resultIndex; i++) {
            finalResult[i] = result[i];
        }

        return string(finalResult);
    }
}

contracts/MinimalCollection.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.12;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

import {URIEncoding} from "./libraries/URIEncoding.sol";


/**
 * @title  Collection Smart Contract
 * @author Hugo Sanchez
 * @notice This is the new collection implementation. It gives users
 *         more control over their work: they own the collection and tokens.
 *         It also allows for social features: members & moderators
 *         as well as read/write permissions for both the collection itself and its tokens.
 */


interface IMarketMaster {
    function executeBuy(uint256 amount, address referer, address creator) external payable;
}

contract MinimalCollection is

    Initializable,
    ERC1155Upgradeable,
    AccessControlUpgradeable,
    ReentrancyGuardUpgradeable

{

    // Collection name.
    string public name;
    // Collection URI.
    string public contractURI;
    // Factory address
    address public factory;
    // Counter for token ids.
    uint256 private _tokenIds;
    // Smart contract version.
    uint256 public constant version = 1;
    // Max supply integer for checks.
    uint256 public constant MAX_SUPPLY = 2**256 - 1;
    // Bytes for moderator role.
    bytes32 public constant MODERATOR_ROLE = keccak256("MODERATOR_ROLE");

    ////////////////////////////
    // MAPPINGS section.
    ////////////////////////////

    // Token id => URI
    mapping(uint256 => string) private _uris;
    // TokenID => Supply
    mapping(uint256 => uint256) public tokenSupply;
    // TokenID => Creator
    mapping(uint256 => address) public creator;
    // TokenID => Post title
    mapping(uint256 => string) public titles;
    // TokenID => Post content
    mapping(uint256 => string) public content;
    // TokenID => Timestamp
    mapping(uint256 => uint256) public createdAt;
    // TokenID => Boolean
    mapping(uint256 => bool) public isGated;

    ////////////////////////////
    // EVENTS section.
    ////////////////////////////

    event NewVersoCreated(address indexed account, uint256 indexed id, string metadataURI);
    event NewVersoCollected(address indexed account, uint256 indexed id, uint256 amount);
    event VersoDeleted(address indexed moderator, uint256 indexed tokenId);
    event URIUpdated(uint256 indexed tokenId, string newUri);
    event ContractURIUpdated(string newUri);

    ////////////////////////////
    // ERRORS section.
    ////////////////////////////

    error InvalidTokenId();
    error MaxSupplyReached();
    error InsufficientBalance();
    error URIAlreadySet();

    ////////////////////////////
    // MODIFIERS section.
    ////////////////////////////

    modifier onlyModeratorsOrFactory() {
        require(hasRole(MODERATOR_ROLE, msg.sender) || msg.sender == factory, "Only moderators allowed");
        _;
    }

    ////////////////////////////
    // INITIALIZER section.
    ////////////////////////////

    function initialize(
        string memory _name,
        string memory _contractURI,
        address _owner
    )
        public
        initializer
    {
        // Set contract name
        name = _name;
        // Set contract metadata URI
        contractURI = _contractURI;
        // Set up factory
        factory = msg.sender;
        // Set up roles
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
        _grantRole(MODERATOR_ROLE, _owner);
        _setRoleAdmin(MODERATOR_ROLE, MODERATOR_ROLE);
        // Initialize ERC1155
        __ERC1155_init(_name);
    }

    ////////////////////////////
    // BASIC SET / GET section.
    ////////////////////////////

    function uri(uint256 tokenId) override public view returns (string memory) {
        if (tokenId < _tokenIds) revert InvalidTokenId();
        return URIEncoding.generateURI(titles[tokenId], content[tokenId], creator[tokenId], address(this), tokenId);
    }

    function setContractURI(string calldata _contractURI) external onlyRole(DEFAULT_ADMIN_ROLE) {
        contractURI = _contractURI;
        emit ContractURIUpdated(_contractURI);
    }

    function updateContent(uint256 tokenId, string memory newContent) public {
        require(tokenId < _tokenIds, "Token does not exist");
        require(creator[tokenId] == msg.sender, "Only the creator can update the content");
        require(block.timestamp <= createdAt[tokenId] + 48 hours, "Update window has expired");
        content[tokenId] = newContent;
    }

    ////////////////////////////
    // CORE functionality
    ////////////////////////////

    /**
     * @dev Creates a new token.
     *
     * @param _title The title of the token.
     * @param _content The content of the token.
     * @param _recipient The recipient of the token.
     * @param _isGated Whether the token is gated.
     */
    function create(
        string calldata _title,
        string calldata _content,
        address _recipient,
        bool _isGated
    )
        external
        onlyModeratorsOrFactory()
    {
        if (_tokenIds >= MAX_SUPPLY) revert MaxSupplyReached();
        _tokenIds++;
        uint256 newTokenId = _tokenIds;
        tokenSupply[newTokenId] = 1;
        creator[newTokenId] = _recipient;
        titles[newTokenId] = _title;
        content[newTokenId] = _content;
        createdAt[newTokenId] = block.timestamp;
        isGated[newTokenId] = _isGated;
        _mint(_recipient, newTokenId, 1, "");
        string memory _url = URIEncoding.generateURI(_title, _content, msg.sender, address(this), newTokenId);
        emit NewVersoCreated(_recipient, newTokenId, _url);
    }


    /**
     * @dev Collects a token.
     *
     * @param id The ID of the token.
     * @param amount The amount of tokens to collect.
     * @param recipient The recipient of the tokens.
     * @param referer The referer of the tokens.
     * @param markerAddress The address of the market master.
     */
    function collect(
        uint256 id,
        uint256 amount,
        address recipient,
        address referer,
        address markerAddress
    )
        external
        payable
        nonReentrant
    {
        if (id > _tokenIds || id == 0) revert InvalidTokenId();
        if (tokenSupply[id] + amount > MAX_SUPPLY) revert MaxSupplyReached();

        IMarketMaster(markerAddress).executeBuy{value: msg.value}(amount, referer, creator[id]);
        _mint(recipient, id, amount, "");
        tokenSupply[id] += amount;
        emit NewVersoCollected(recipient, id, amount);
    }

    /**
     * @dev Burns tokens.
     *
     * @param tokenId The ID of the token to burn.
     * @param amount The amount of tokens to burn.
     */
    function burn(uint256 tokenId, uint256 amount) external nonReentrant {
        if (balanceOf(msg.sender, tokenId) < amount) revert InsufficientBalance();
        _burn(msg.sender, tokenId, amount);
        tokenSupply[tokenId] -= amount;
    }

    ////////////////////////////
    // SAFETY functions
    ////////////////////////////

     function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

